import { useEffect, useState } from 'react';
import './App.css';
import Artist from './Artist';

function App() {

    const [todos, setTodos] = useState([]);
    const [page, setPage] = useState(1);

    function prevPage() {
        if (page === 1) {
            return;
        }
        setPage(page - 1);
    }

    function nextPage() {
        setPage(page + 1);
    }

    useEffect(() => {
        fetch(`http://dummyjson.com/todos/?skip=${(page - 1) * 50}&limit=50`)
            .then(res => res.json())
            .then(json => {
                setTodos(json['todos']);
            })
    }, [page]);

    return (
        <>
            <p>Page {page}</p>
            <div>Nombre de todo : {todos.length}</div>
            {
                todos.map((todo) => {
                    return <Todo title={todo['todo']} isChecked={todo['completed']} key={todo['id']} />;
                })
            }
            <button onClick={prevPage}>Page Precedante</button>
            <button onClick={nextPage}>Page Suivante</button>
        </>
    );
}

export default App;
